monitor=512/8;

warp=0;
q1=time*0.4;
q2=8; //cubesize

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + (0.5 - rand_frame.xy)));
 vec4 tmpvar_3;
 tmpvar_3 = texture (sampler_main, uv);
 ret_1.z = tmpvar_3.z;
 ret_1.x = ((tmpvar_3.x - (1.4 * q6)) * (1.0 - (q6 * 2.5)));
 vec4 tmpvar_4;
 tmpvar_4 = clamp (((
   (texture (sampler_blur1, ((uv_orig + (0.005 * tmpvar_2).xy) - texsize.zw)).xyz * scale1)
  + bias1).x - (
   (tmpvar_2 - 0.5)
  * 0.15)), 0.0, 1.0);
 float tmpvar_5;
 tmpvar_5 = (tmpvar_3.z + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1).z * 0.2));
 ret_1.y = 0.0;
 if ((tmpvar_4.x > 0.1)) {
   ret_1.x = ((tmpvar_5 * q6) * 160.0);
   ret_1.z = (tmpvar_3.z - ((tmpvar_3.z * q6) * 120.0));
 } else {
   ret_1.z = (ret_1.z + (1.3 * q6));
 };
 vec4 tmpvar_6;
 tmpvar_6.w = 1.0;
 tmpvar_6.xyz = ret_1;
 ret = tmpvar_6.xyz;
}

