{
    float2 noise_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + (0.5-rand_frame.xy);
    float4 noiseVal = (tex2D(sampler_noise_lq, noise_uv));

float2 adj = texsize.zw;
//float2 adj = 0;

float2 uvn = uv_orig + .005*noiseVal + float2(-1,-1)*adj;

// sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
// darken (decay) over time
