init_eqs_eel:
mv_x=64;mv_y=48;
dcsp=0;

zoom = 1.0;
warp = 0;
rot = 0;
dx = 0;
dy = 0;
sx = 1;
sy = -1;
cx = 0.5;
cy = 0.5;

frame_eqs_eel:
decay = 0.97;

//uncommenting this block will make decay start at 0 when preset begins, and rise to the "max_decay" value
//max_decay = 0.97;
//dcsp = dcsp + 0.007*(75/fps);
//dcsp = min(dcsp , max_decay);
//decay = dcsp;

//volume, and volume based time. framerate adjusted
vol = (bass+mid+treb)*0.25;
vol = vol*vol;

mtime = mtime + vol * 0.01 * (75/fps);

q1 = vol;
q2 = time/9;//3.1415/2 + sin(time/3)*0.13;

//non bass volume
vol2 = (mid+treb)*0.33;
vol2 = vol2*vol2;

m2time = m2time + vol2 * 0.01 * (75/fps);

q3 = vol2;
q4 = m2time;


//krash's beat detection code
volume = 0.3*(bass+mid+att);
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01) + (1-below(volume,0.01))*beatrate);
lastbeat = lastbeat + equal(lastbeat,0)*time;
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
peakbass_att = max(bass_att,peakbass_att);
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = beat*bass_att + (1-beat)*peakbass_att*(above(time - lastbeat, 2*beatrate)*0.95 + (1-above(time - lastbeat, 2*beatrate))*0.995);
lastbeat = beat*time + (1-beat)*lastbeat;
peakbass_att = max(peakbass_att,1.1*meanbass_att);
beat = above(volume,0.8)*below(peakbass_att - bass_att, 0.05*peakbass_att)*above(time - lastbeat, 0.1 + 0.5*(beatrate - 0.1));
beatcounter = beatcounter + beat;
mode = if(beat*equal(beatcounter%2,0),1-mode,mode);
flip = 2*mode-1;

beatdrop = if( equal(beatdrop,0) , beat , max(beatdrop - 0.02*(75/fps) , 0) );

q7 = beatdrop;

q8 = 12;

bd = min( vol*0.5 , 1 );

gamma = 1 + min(vol*0.7,1);//bd*0.98;
decay = 0.95;

//zoom = 1 + bd*0.1;

sx=-1;

pixel_eqs_eel:
zoom = 1 + rad*rad*0.15;

size = 0.2;
center = rad - size;
center = center * below(center,0);
center = -center / 0.2;
zoom = zoom - center*q1*12;
