pixel_eqs_eel:
//use simple fuzzy logic to model zooming

//rules
fuzzy1 = x*0.1*rad;
fuzzy3 = (x-1)*0.1*rad;
fuzzy2 = y*0.1*rad;
fuzzy4 = (y-1)*0.1*rad;

//activations approx sawtooth with sin curve
f1 = sin(q4 - 1.5707);
f2 = sin(q4);
f3 = sin(q4 + 1.5707);
f4 = sin(q4 + 3.14);

//take only values above zero
f1 = f1*above(f1,0);
f2 = f2*above(f2,0);
f3 = f3*above(f3,0);
f4 = f4*above(f4,0);

//sum rule ativations
sum_fuzzy = fuzzy1*f1 + fuzzy2*f2 - fuzzy3*f3 - fuzzy4*f4;

zoom = 0.975 - (rad- 0.5)*0.05 + sum_fuzzy*0.5;
