shapes.frame_eqs_eel:
/////// Planetary system \\\\\\\

///// 3+3+1 elliptical trajectories

sample=instance/num_inst;
t1=.30*time+q1;
t2=.25*time+q2;
t3=.35*time+q3;

/////// planets trajectories
x1= 10*rad*sin(20*sample+3*t1);
y1= 10*rad*cos(20*sample+2*t1);
z1= 0;

x2= 0;
y2= x1;
z2= y1;

x3= y1;
y3= 0;
z3= x1;

x=if(above(sample,.33), if(above(sample,.66),x3,x2), x1);
y=if(above(sample,.33), if(above(sample,.66),y3,y2), y1);
z=if(above(sample,.33), if(above(sample,.66),z3,z2), z1);

a=if(equal(instance%15,0),1,a);


/////// satellites trajectories
x01= 3*rad*sin(20*sample+5*t2);
y01= 3*rad*cos(20*sample+5*t2);
z01= 0;

x11= x2+ x01;
y11= y2+ y01;
z11= z2+ z01;

x12= x3+ z01;
y12= y3+ x01;
z12= z3+ y01;

x13= x1+ y01;
y13= y1+ x01;
z13= z1+ z01;

x=if(above(sample,0)*below(sample,.03),x11,x);
y=if(above(sample,0)*below(sample,.03),y11,y);
z=if(above(sample,0)*below(sample,.03),z11,z);

x=if(above(sample,.4)*below(sample,.43),x12,x);
y=if(above(sample,.4)*below(sample,.43),y12,y);
z=if(above(sample,.4)*below(sample,.43),z12,z);

x=if(above(sample,.7)*below(sample,.72),x13,x);
y=if(above(sample,.7)*below(sample,.72),y13,y);
z=if(above(sample,.7)*below(sample,.72),z13,z);

a=if(above(sample,0)*below(sample,.03),1,a);
a=if(above(sample,.4)*below(sample,.43),1,a);
a=if(above(sample,.7)*below(sample,.72),1,a);
rad=if(above(sample,0)*below(sample,.03),.5*rad,rad);

/////// sub-satellite trajectorie
x21= x13+1*rad*sin(20*sample+10*t3);
y21= y13+1*rad*cos(20*sample+10*t3);
z21= z13;

x=if(above(sample,.69)*below(sample,.7),x21,x);
y=if(above(sample,.69)*below(sample,.7),y21,y);
z=if(above(sample,.69)*below(sample,.7),z21,z);

rad=if(above(sample,.69)*below(sample,.7),.2*rad,rad);
a=if(above(sample,.69)*below(sample,.7),1,a);


//a2=a;
rad=2.5*rad*sin(31*sample);

r=.8+.2*sin(20*sample);
g=.8+.2*sin(130*sample);
b=.8+.2*sin(140*sample);


my_x = x;
my_y = y;
my_z = z;


/////// rotations... by flexi

d = .8+.3*sin(.4*t1);
zoom = .7;

w3 = q1;
w2 = q2;
w1 = q3;

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
p = tan(asin(1) + atan2(d+z3,l));
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));

rad=rad/d;
my_x = zoom*sin(w)*p;
my_y = zoom*cos(w)*p;

x = 0.5 + my_x/q5 ;
y = 0.5 + my_y/q4 ;

frame_eqs_eel:
warp=0;
wave_a=0;
zoom=1;
decay=.65;


//// flexi's beat counter
bt1 = bt1*0.95 + sqr(bass)*30/fps;
mt1 = mt1*0.95 + sqr(mid)*30/fps;
tt1 = tt1*0.95 + sqr(treb)*30/fps;
bt2 = bt2 + bt1*0.001;
mt2 = mt2 + mt1*0.001;
tt2 = tt2 + tt1*0.001;

q1 =.2*bt2;
q2 =.2*mt2;
q3 =.2*tt2;

q4=aspectx;
q5=aspecty;
