warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_fc_main, uv).xyz * 0.8);
 ret_1 = (ret_1 + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.2));
 ret_1 = (ret_1 * (ret_1 / (1.4 * rad)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 1.2));
 ret_1 = (pow (ret_1, vec3(0.5, 0.7, 1.4)) * 1.25);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz*.8;
    ret += GetBlur1(uv)*.2;

    // darken (decay) over time and distance from center
    ret *= ret/(1.4*rad); //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*1.2;
    ret = pow(ret, float3(0.5,0.7,1.4))*1.25;
}
