temp3=.1*cos((q4*1.473+3.1415*q6)+0.1*mid_att);
temp4=.35*sin((q4*1.473+3.1415*q6)+0.1*mid_att)*1.2;
temp5=.1*cos((q5*1.473+3.1415*q6)+0.1*treb_att);
temp6=.35*sin((q5*1.473+3.1415*q6)+0.1*treb_att)*1.2;

v1=temp1*cos(0*time*0.1+1.0467)-temp2*sin(0*time*0.1+1.0467)+.5;
v2=temp1*sin(0*time*0.1+1.0467)+temp2*cos(0*time*0.1+1.0467)+.5;
v3=temp3*cos(0*time*0.1+2.0933)-temp4*sin(0*time*0.1+2.0933)+.5;
v4=temp3*sin(0*time*0.1+2.0933)+temp4*cos(0*time*0.1+2.0933)+.5;
v5=temp5*cos(0*time*0.1)-temp6*sin(0*time*0.1)+.5;
v6=temp5*sin(0*time*0.1)+temp6*cos(0*time*0.1)+.5;

tmp=v1*(v4-v6)-v2*(v3-v5)+(v3*v6-v5*v4);
xcoef=(v1*v1+v2*v2)*(v4-v6)-(v3*v3+v4*v4)*(v2-v6)+(v5*v5+v6*v6)*(v2-v4);
ycoef=(v1*v1+v2*v2)*(v3-v5)-(v3*v3+v4*v4)*(v1-v5)+(v5*v5+v6*v6)*(v1-v3);
coef=(v1*v1+v2*v2)*(v3*v6-v5*v4) - (v3*v3+v4*v4)*(v1*v6-v5*v2) +(v5*v5+v6*v6)*(v1*v4-v3*v2);

xcoef=-xcoef/tmp;
ycoef=ycoef/tmp;
coef=-coef/tmp;

// circle coordinates (t1,t2,t3)=(centerX,centerY,radius)
t1=-xcoef*.5;
t2=-ycoef*.5;
t3=sqrt(.25*(xcoef*xcoef+ycoef*ycoef-4*coef));

// the angles of the electrons with respect to the circle's center
// (add 2pi to avoid negative values)
v7=atan2(v2-t2,v1-t1)+6.2831;
v8=atan2(v4-t2,v3-t1)+6.2831;
v9=atan2(v6-t2,v5-t1)+6.2831;

// sort v7,v8,v9 (from smaller to bigger) and save in t4,t5,t6.
t4=min(v7,v8); t4=min(t4,v9);
t6=max(v7,v8); t6=max(t6,v9);
t5=if( above(v7-t4,0)*below(v7-t6,0), v7,0);
t5=if( above(v8-t4,0)*below(v8-t6,0), v8,t5);
t5=if( above(v9-t4,0)*below(v9-t6,0), v9,t5);

// t4,t5,t6 now are the sorted angles of the electrons and t4<t5<t6.
// the arcs (t4,t5),(t5,t6),(t6,t4) cover the entire circumference.
// we must find which of all possible pairs has a minimum sum of lengths.
//   only those 2 arcs must be drawn.
v1=t6-t5+t5-t4;
v2=t6-t5+t4+6.2831-t6;
v3=t5-t4+t4+6.2831-t6;
v4=min(min(v1,v2),v3);

// the circle will be plotted only for angles within (t7...t8)
// t7:plot start angle, t8:plot final angle
t7=if( equal(v4,v1), t4,t7);
t8=if( equal(v4,v1), t6,t8);
t7=if( equal(v4,v2), t5,t7);
t8=if( equal(v4,v2), t4+6.2831,t8);
t7=if( equal(v4,v3), t6,t7);
t8=if( equal(v4,v3), t5+6.2831,t8);

