shapes.frame_eqs_eel:
sample=instance/num_inst;

///// spiral

x = sample*sin(30*sample-3*time);
y = sample*cos(30*sample-3*time);

x = 0.51 + x/q6;
y = 0.5 + y/q5;

frame_eqs_eel:
decay=1;
wave_a=0;
zoom=1;
warp=0;

q5=aspectx;
q6=aspecty;

comp:
shader_body { 
 vec2 uv_1;
 vec2 uv3_2;
 vec2 uv2_3;
 vec3 ret_4;
 uv_1 = (uv - 0.5);
 uv_1 = (uv_1 * aspect.xy);
 uv2_3.x = ((uv_1.x * -0.497265) - (uv_1.y * 0.8675987));
 uv2_3.y = ((uv_1.x * 0.8675987) + (uv_1.y * -0.497265));
 uv3_2.x = ((uv_1.x * -0.5000263) - (uv_1.y * -0.8660102));
 uv3_2.y = ((uv_1.x * -0.8660102) + (uv_1.y * -0.5000263));
 ret_4 = (texture (sampler_main, (uv_1 + 0.5)).xyz - (roam_sin.yzw * roam_cos.xyz));
 vec3 tmpvar_5;
 tmpvar_5 = max (max (ret_4, texture (sampler_main, (uv2_3 + 0.5)).xyz), texture (sampler_main, (uv3_2 + 0.5)).xyz);
 ret_4 = tmpvar_5;
 vec4 tmpvar_6;
 tmpvar_6.w = 1.0;
 tmpvar_6.xyz = tmpvar_5;
 ret = tmpvar_6.xyz;
}

comp_hlsl:
shader_body
{
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 6.28*0.333;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 6.28*0.667;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
//ret-=roam_sin*roam_cos.wzy;
ret-=roam_sin.yzw*roam_cos;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).xyz);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).xyz);

    //ret *= 1.3; // a little bit of overbright
}
