warp_hlsl:
shader_body
{

   // sample previous frame
ret = tex2D( sampler_fc_main, lerp(uv,uv_orig,.15)).xyz;

ret.x += frac(GetPixel(uv+float2(1,2)*texsize.zw*GetPixel(uv).x).x);
ret.x -= GetBlur3(uv).x;

ret.y += frac(GetPixel(uv+float2(2,-2)*texsize.zw*GetPixel(uv).y).y);
