warp_hlsl:
shader_body
{
float2 uv2=uv;
float3 texm=0;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // mix in code based off of Geiss's original mix code
    float pic = tex2D(sampler_noise_hq, uv_orig*aspect.xy*.25+.1*roam_cos+.1*time).xyz;
    float lum = lum(pic);
    float use_it = abs(lum+.2 - (0.5-roam_cos.xy*0.15));
    float LFNoise = tex2D(sampler_noise_hq, pic-(uv.yx*2 + (time*.15)));
    use_it = (1-use_it - (8*LFNoise));
    float tex = lerp(ret, pic, 1.2*use_it);

texm=tex;
