cx = cx + 0.110*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
cy = cy + 0.110*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
q1=cos(1.41*time);
q2=time + 0.3*sin(time*1.47);

// this is a great way to respond to beats:
// once you get one, let it decay at a constant rate!!
rg = max(rg*0.77, 1.2 + 1.2*min(2,max(0,mid_att-1)*1.3));
q9 = rg * above(rg,rg2*2);
rg2 = max(rg, rg2*0.8);

//zoom = zoom + q9*0.1;
monitor = q9;

// for custom shape 1:
q11 = aspectx;
q12 = aspecty;
q13 = wave_r;
q14 = wave_g;
q15 = wave_b;

// custom beat detection code: (fps-independent; quiet songs don't freak out)
min_att    = 2.5;               // lower # = quieter songs can declare beats
decay_to   = 0.8;               // lower # = more hasty to declare a beat
decay_rate = pow(0.9980, fps);  // lower # = more hasty to declare a beat
beat =           bass/max(min_att,bass_att);
beat = max(beat, mid /max(min_att,mid_att ));
beat = max(beat, treb/max(min_att,treb_att));
beat = max( beat, (prev_beat-decay_to)*decay_rate + decay_to );
beat_level     = (beat - prev_beat - 0.07)*24;
is_beat = above(beat_level, 0.5);
  // effects:
  warp = (beat - prev_beat)*22;
  q7 = (beat - prev_beat - 0.08)*32;
prev_beat = beat;

