shader_body { 
 vec2 uv_1;
 vec2 tmpvar_2;
 tmpvar_2 = normalize((uv_orig - 0.5));
 float tmpvar_3;
 tmpvar_3 = (time * 7.0);
 uv_1 = (mix (uv_orig, uv, vec2((
   clamp ((((bass / bass_att) - 0.9) * 13.0), 0.0, 1.0)
  * 0.3))) + ((
   (tmpvar_2 * texsize.zw)
  * 
   cos(((rad * 170.0) - tmpvar_3))
 ) * 2.0));
 uv_1 = (uv_1 + ((
   (vec2(5.0, -5.0) * texsize.zw)
  * 
   cos(((ang * 30.0) - tmpvar_3))
 ) * tmpvar_2.yx));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((texture (sampler_main, uv_1).xyz - 0.004) * 0.83);
 ret = tmpvar_4.xyz;
}

