warp_hlsl:
shader_body
{
    // sample previous frame
   float3 pre = tex2D( sampler_main, uv_orig);
   float3 warped = tex2D( sampler_main, uv -floor(uv));

  ret = lerp( pre, warped, 0.5);
    // darken (decay) over time
    ret *= 0.90; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 +
              GetBlur1(uv)*3 +
              GetBlur2(uv)*0 +
