frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %4;
index2 = (index2 + is_beat*bnot(index))%4;
monitor = index2;
q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;

k1 =  is_beat*bnot(index)*bnot(index2);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
p3 = dec_med * p3+ (1-dec_med)*p2;
rott = p3 * 3.1416/4;

pos = pos + q20/140;
q28 = pos;
q27 = index+1;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

movez = movez + +.005*(1.5+q1)*30/fps;
q30 = movez;


zoom = 1.0;
rot = -0.00 * index;

