frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%3;
index4 = (index4 + is_beat*bnot(index)*bnot(index2)*bnot(index3))%8;
monitor = index4;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;

//k1 =  is_beat*bnot(index)*bnot(index2);

k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.14159265359/2;

q27 = 8-index;
q28 = index3;
q29 = index4;

stop = equal (index,4);
stop2 = .9 * stop2 + .1 * stop;
accel = stop - stop2;

//movez = movez + .01*(2+ q21)*30/fps;
movez = movez + .01*4*30/fps;
q30 = movez;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

zoom = 1.0;
q32 = pow(0.996, 30/fps); //fade

