sampler sampler_rand01_OI;

float figure_borders(float2 domain, float thickness){
   float2 help_uv = frac(domain-0.5);
   help_uv = min(help_uv,1-help_uv)*2;
   return saturate(max(help_uv.x,help_uv.y)-1+thickness)/(thickness);
}

bool hex(float2 domain){
   float2 help_uv = frac(domain)*float2(8,12);
   float x,y; x = help_uv.x; y = help_uv.y;
   return (x>1)*(x<7)*(y>4)*(y<8)*(x+y>7)*(x+y<13)*(x-y<1)*(y-x<5);
}

bool3 hexgrid(float2 domain){
   bool tile1, tile2;
   tile1 = hex(domain) + hex(domain + 0.5);
   tile2 = hex(domain + float2(0.5,1/6.0) )
         + hex(domain + float2(0,4/6.0)   );
   return bool3(tile1, tile2,  1 - tile1 - tile2);
}


float2 complex_div(float2 numerator, float2 denominator){
   return float2( numerator.x*denominator.x + numerator.y*denominator.y,
                  numerator.y*denominator.x - numerator.x*denominator.y)/
          (denominator.x*denominator.x + denominator.y*denominator.y);
}

float2 uv_moebius_transformation(float2 domain, float2 zeroPoint, float2 infinityPoint,float zoom){
   return complex_div((domain - zeroPoint)*zoom, domain - infinityPoint)+0.5;
}

float2 uv_polar(float2 domain, float2 center){
   float2 c = domain - center;
   float rad_hq = length(c);
   float ang_hq = atan2(c.x,c.y);
   return float2(ang_hq, rad_hq);
}

float2 uv_polar_logarithmic_inverse(float2 domain, float2 center, int fins, float log_factor, float2 coord){
   float2 polar = uv_polar(domain, center)*float2(M_INV_PI_2,1);
   return float2(polar.x*fins+coord.x, log_factor*log(1/polar.y) + coord.y);
}

float2 uv_bipolar_logarithmic_inverse(float2 domain, float2 northPole, float2 southPole, int fins, float log_factor, float2 coord){
   float2 help_uv = uv_moebius_transformation(domain, northPole, southPole, 1);
   return uv_polar_logarithmic_inverse(help_uv,0.5,fins,log_factor,coord);
}

float2 uv_lens_half_sphere(float2 domain, float2 position, float radius, float refractivity){
   float2 polar = uv_polar(domain, position);
   float cone = saturate(1-polar.y/radius);
   float halfsphere = sqrt(1-pow(cone-1,2));
   float w = atan2(1-cone,halfsphere);
   float refrac_w = w-asin(sin(w)/refractivity);
   float refrac_d = 1-cone - halfsphere*tan(refrac_w);
   float2 refrac_uv =position+float2(sin(polar.x),cos(polar.x))*refrac_d*radius;
   bool mask =(length(domain-position)<radius);
   return !mask*domain + 
           mask*refrac_uv;
}

