frame_eqs_eel:
xlen = 45; ylen = 45; flen = 20; reg00 = xlen*ylen/4; reg01=reg00/4;
dec_med = 1-0.06*30/fps; dec_slow = 1-0.02*30/fps; dec_f = pow (0.8, 30/fps);
q30 = dec_slow;
smooth = max(1,pow(6,fps/30)-2); // >= 0; Ausbreitungsgeschwindigkeit

//"Classic" beat reaction
cbeat = bass+mid+treb;
decc = if (vol>cbeat,0.8,dec_med);
vol  = vol * decc + (1-decc)*cbeat;
avg = avg*dec_slow + cbeat*(1-dec_slow);
is_beat = above(cbeat, 1.5*avg) * above (time, tc0+.2);
tc0 = if (bor(is_beat, bd_bt),time,tc0);
peak = if (is_beat,cbeat,peak*dec_med);


//Gleichanteil entfernen und normalisieren
ind = 0; sum = 0; amp = 0.01;
loop (reg01,
  sum = sum + gmegabuf(ind+30000)/(reg01);
  amp = amp + pow(gmegabuf(ind+30000),2);
  ind = ind+1;
); 
ind = 0; amp_= amp_*dec_med + (1-dec_med)*sqrt(amp)/reg01*600;
loop (reg01,
  megabuf(ind+30000)= (gmegabuf(ind+30000)-sum)/amp_;
  ind = ind+1;
);

// Spectrum of spectrum  !
index2 = 0;
loop(flen, //Frequency step
  index=0; real= 0; imag = 0;
  loop (flen,
    real = real + cos(index/flen*6.28*index2)*megabuf(index*reg01/8+30002);
    imag = imag + sin(index/flen*6.28*index2)*megabuf(index*reg01/8+30002);
    index=index+1;
  );
  megabuf(10000+index2)= megabuf(10000+index2) * dec_f + real;
  megabuf(15000+index2)= megabuf(15000+index2) * dec_f + imag;
  index2 = index2+1;
 );

//Anregung
ind = 1;
loop (flen/2,
  cx = megabuf(10000+ind);
  cy = megabuf(15000+ind);
  yind = -1;
  loop (3,
    xind = -1;
    loop (3,
      ox = ((cx+.5)*xlen+xind)%xlen;
      oy = ((cy+.5)*ylen+yind)%ylen;
      amp = (cx*cx+cy*cy)*3;
      megabuf (oy*ylen+ox) = megabuf (oy*ylen+ox) 
                           - 2*30/fps*sqrt(amp)*above(amp,.02)/(1+xind*xind+yind*yind);
      xind = xind+1;
    );
    yind = yind+1;
  );
  ind = ind+1;
);

//Simulation
yind = 0;
loop (ylen,
  xind = 0;
  loop (xlen,
    megabuf(yind*ylen+xind+5000) = //megabuf ab 5000 ist nur Zwischenspeicher
    dec_med*((gmegabuf(yind*ylen+(xind+1)%xlen) +
    gmegabuf(yind*ylen+(xlen+xind-1)%xlen) + 
    gmegabuf(((yind+1)%ylen)*ylen+xind) + 
    gmegabuf(((yind+ylen-1)%ylen)*ylen+xind)
    +gmegabuf(yind*ylen+xind)*smooth*4)/(2+2*smooth) - megabuf(yind*ylen+xind)); 
    xind = xind+1;
  );
  yind = yind+1;
);

//Projektionsparameter

bindex += is_beat;

if (is_beat && (bindex%4==0), ran2 = (rand(100)-50)/50,0 );
if (is_beat && (bindex%4==2), ran3 = (rand(100)-50)/50,0 );
if (is_beat && (bindex%6==5), ran4 = (rand(100)-50)/50,0 );

trelx += ran2/fps/18;
trely += ran3/fps/2;
trelz += ran4/fps/6;
zang = 6*sin(trelz);
xang = 6*sin(zang/5+trelx);
yang = 6*sin(zang/3*0+trely);

q1 = cos(xang);q2 = sin(xang);q3 = cos(yang);
q4 = sin(yang);q5 = cos(zang);q6 = sin(zang);
fov = 1;

//Umkopieren und Projektion
yind = 0;
loop (ylen,
  xind = 0;
  loop (xlen,
    ind = yind*ylen+xind;
    megabuf(ind) = gmegabuf(ind); 
    gmegabuf(ind) = megabuf(ind+5000); 
    oz = yind - ylen/2; //#
    ox = if(bnot(yind%2),xind,xlen-xind-1);
    oy0a = (oy0+oy0a)/2.5;
    oy = gmegabuf(yind*ylen+ox); oy0 = oy;
    ox = ox - xlen/2;  
    mx = ox*q5 - oy*q6;  my = ox*q6 + oy*q5;
    ox = mx; oy = my;  
    mx = ox*q3 + oz*q4;  mz = - ox*q4 + oz*q3;
    ox = mx; oz = mz;  
    my = oy*q1 - oz*q2;  mz = oy*q2 + oz*q1;
    z0 = (10*sin(trelz*14));
    oy = my; oz = mz+z0;
    gmegabuf(10000+ind) = fov*ox/(oz+60*fov)+.5;
    gmegabuf(15000+ind) = fov*oy/(oz+60*fov)+.5;
    hell= max(min(.5+oy0/4,1),0.2); //Helligkeit
    gmegabuf(20000+ind) = max(min(hell* (.5+ (oy0a-oy0)*0.1),1),0.1);
    gmegabuf(25000+ind) = oy0/16+.5;;
    xind = xind+1;
  );
  yind = yind+1;
);

q29 = 10/(z0+15);
q32 = aspecty;
q28 = min (vol/3-0.25,1);
monitor = q28;

warp:
shader_body { 
 vec2 v_1;
 vec3 ret_2;
 v_1 = (normalize((uv - 0.5)).yx * vec2(1.0, -1.0));
 v_1 = (v_1 * (texsize.zw * 3.0));
 ret_2 = (0.2 * ((
   ((texture (sampler_main, uv).xyz + texture (sampler_main, (uv + v_1)).xyz) + texture (sampler_main, (uv - v_1)).xyz)
  + texture (sampler_main, 
   (uv + (v_1 * 2.0))
 ).xyz) + texture (sampler_main, (uv - 
   (v_1 * 2.0)
 )).xyz));
 ret_2 = (ret_2 * (1.0 + (0.02 * 
   cos((rad * 177.0))
 )));
 ret_2 = (ret_2 * 0.98);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (mix (texture (sampler_main, uv).xyz, (
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1), vec3(0.5, 0.5, 0.5)) * 1.2);
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame 3 times, creating angular blur
    float2 v = normalize(uv - 0.5);
    v = v.yx * float2(1,-1);
    v *= texsize.zw*3;
    ret = 0.2*( tex2D( sampler_main, uv ).xyz
                   +  tex2D(sampler_main, uv + v ).xyz
                   +  tex2D(sampler_main, uv - v).xyz
                   +  tex2D(sampler_main, uv + v*2 ).xyz
                   +  tex2D(sampler_main, uv - v*2).xyz
    );

    ret *= 1 + 0.02*cos(rad*177);

    // darken over time
    ret *= 0.98; //or try: ret -= 0.004;
}

