rad=.05+.04*sin(sample);

/////  Knot

q10=250*sample;
my_x = -.22*cos(q10)-1.28*sin(q10)-.44*cos(3*q10)-.78*sin(3*q10);
my_y= -.1*cos(2*q10)-.27*sin(2*q10)+.38*cos(4*q10)+.46*sin(4*q10);
my_z=.7*cos(3*q10)-.4*sin(3*q10);


/////// rotations... by flexi

d = .2; 
zoom = 1;

w3 = q1;
w2 = q2;
w1 = q3;

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

// perspective calculation

l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
p = tan(asin(1) + atan2(d+z3,l));
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));

my_x = zoom*sin(w)*p;
my_y = zoom*cos(w)*p;

p1 = .2*sin(sample*50);
p2 = .2*sin(sample*80);

x = .5+my_x*p1;
y = .5+my_y*p2;

r = .4*sin(sample*80);
b = .8*sin(sample*66);
g = .6*sin(sample*120);

