frame_eqs_eel:
zoom = .995;
wave_a = 0;
mv_g = 0.45 + 0.3*sin(time*1.33)+0.25*sin(treb);
mv_r = 0.45 + 0.3*sin(time*1.13)+0.25*(0.33*(bass+treb+mid));
mv_b = 0.45 + 0.3*sin(time*1.23)+0.25*sin(bass);
mv_dx=.1*sin(time*.1);
mv_dy=.13*cos(time*.084);
mv_x=64-int((sin(time*1.5)+1)*32);
mv_y=48-int((cos(time*1.5)+1)*22);
monitor=mv_y;

pixel_eqs_eel:
//flexi per vertex
cx1 = 0.5+sin(time*0.618)*0.2;
cy1 = 0.5+cos(time*1.618)*0.2;
dir = bass;
d = sqrt((x-cx1)*(x-cx1)+(y-cy1)*(y-cy1));
x1 = if( above(d,0.3),0,  sin(y-cy1)*0.05*dir);
y1 = if( above(d,0.3),0, -sin(x-cx1)*0.05*dir);
cx1 = 0.5+sin(time*2.618)*0.3;
cy1 = 0.5+cos(time*3.14)*0.3;
dir = -mid*2;
d = sqrt((x-cx1)*(x-cx1)+(y-cy1)*(y-cy1));
x2 = if( above(d,0.2),0,  sin(y-cy1)*0.05*dir);
y2 = if( above(d,0.2),0, -sin(x-cx1)*0.05*dir);
cx1 = 0.5+sin(-time*2.618)*0.4;
cy1 = 0.5+cos(-time*1.14)*0.4;
dir = treb*3;
d = sqrt((x-cx1)*(x-cx1)+(y-cy1)*(y-cy1));
x3 = if( above(d,0.1),0,  sin(y-cy1)*0.05*dir);
y3 = if( above(d,0.1),0, -sin(x-cx1)*0.05*dir);
dx = dx+x1+x2+x3;
dy = dy+y1+y2+y3;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 vec2 tmpvar_2;
 tmpvar_2 = (normalize((uv - uv_orig)) * texsize.zw);
 vec4 tmpvar_3;
 tmpvar_3.w = 0.0;
 tmpvar_3.xyz = ret_1;
 vec4 tmpvar_4;
 tmpvar_4.w = 0.0;
 tmpvar_4.xyz = max (tmpvar_3, texture (sampler_main, (uv + (tmpvar_2 * 2.3)))).xyz;
 ret_1 = (max (tmpvar_4, texture (sampler_main, (uv - 
   (tmpvar_2 * 2.3)
 ))).xyz * 0.97);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_1;
 ret = tmpvar_5.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = (abs((
   ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  - texture (sampler_main, uv_1).xyz)) * 6.0);
 ret_2 = (ret_2 * 1.333);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // this vector points exactly one pixel, in the direction of motion
    float2 v = normalize(uv-uv_orig)*texsize.zw;

    float3 s;
    ret = max(ret, tex2D(sampler_main, uv+v*2.3));
    ret = max(ret, tex2D(sampler_main, uv-v*2.3));

    // darken over time
    ret *= 0.97;

