vol_avg = .1*(vol_avg*9 + vol);
vg = vol_avg*.1;
t1 = if(above(vg,1.8),1.8,vg);

waves.point_eqs_str:
tm = q1;
sp = sample*6.28*8*6;

vol = (value1+value2)*.5;
it = it*above(sample,0);
it = it + 1;
rad = .5 + vol;
ra = rad*sin(sample*3.14);
ox = ra*sin(sp);
oy = sin(sample*3.14-1.57)*rad;
oz = ra*cos(sp);

xang = tm*.132;
sxang = 0;
yang = tm*.153;
ayang = 0;
zang = tm*.110;
azang = 0;
fov = 0.6 + 0.2*sin(tm);
fov = .5;

mx = ox*cos(zang) - oy*sin(zang);
my = ox*sin(zang) + oy*cos(zang);
ox = mx;
oy = my;
mx = ox*cos(yang) + oz*sin(yang);
mz = - ox*sin(yang) + oz*cos(yang);
ox = mx;
oz = mz;
my = oy*cos(xang) - oz*sin(xang);
mz = oy*sin(xang) + oz*cos(xang);
oy = my;
oz = mz;

oz = oz - 2;
x = ox*fov/oz + 0.5;
x = (x-.5)*0.75 + 0.5;
y = oy*fov/oz + 0.5;

r = 1;
g = .25+.25*sin(sp);
b = 0;
a = .5 + (oz+2)*.5;
a = a*below(z,2);
minrgb = min(r,min(g,b));
maxrgb = max(r,max(g,b));
l = (maxrgb-minrgb)*.5;
diff = maxrgb-minrgb;
sum = maxrgb+minrgb;
s = if(above(l,0.5),diff/(2-sum),diff/sum)*(1-equal(l,0));
h = if(equal(r,maxrgb),(g-b)/diff,if(equal(g,maxrgb),2+(b-r)/diff,4+(r-g)/diff));
h = h*0.1666666;
h = if(below(h,0),0,if(above(h,1),1,h));

h = h + time*0.05*1.324;
h = h - int(h);

tmpb = if(below(l,0.5),l*(1+s),(l+s)-(s*l));
tmpa = 2*l - tmpb;
hvr = h + .333333;
hvr = if(below(hvr,0),hvr+1,if(above(hvr,1),hvr-1,hvr));
hvg = h;
hvg = if(below(hvg,0),hvg+1,if(above(hvg,1),hvg-1,hvg));
hvb = h - .333333;
hvb = if(below(hvb,0),hvb+1,if(above(hvb,1),hvb-1,hvb));

r = if(below(6*hvr,1),tmpa+(tmpb-tmpa)*6*hvr, if(below(2*hvr,1),tmpb, if(below(hvr*3,2),tmpa+(tmpb-tmpa)*(.666666-hvr)*6,tmpa)));
g = if(below(6*hvg,1),tmpa+(tmpb-tmpa)*6*hvg, if(below(2*hvg,1),tmpb, if(below(hvg*3,2),tmpa+(tmpb-tmpa)*(.666666-hvg)*6,tmpa)));
b = if(below(6*hvb,1),tmpa+(tmpb-tmpa)*6*hvb, if(below(2*hvb,1),tmpb, if(below(hvb*3,2),tmpa+(tmpb-tmpa)*(.666666-hvb)*6,tmpa)));

