warp:
shader_body { 
 float final_noise_1;
 vec3 tmpvar_2;
 tmpvar_2.xy = uv;
 tmpvar_2.z = (q2 * 10.0);
 final_noise_1 = ((texture (sampler_noisevol_lq, tmpvar_2) * 2.0) - 1.0).x;
 final_noise_1 = (final_noise_1 + (0.5 * (
   (texture (sampler_noisevol_lq, (tmpvar_2 * 2.0)) * 2.0)
  - 1.0)).x);
 final_noise_1 = (final_noise_1 + (0.25 * (
   (texture (sampler_noisevol_lq, (tmpvar_2 * 4.0)) * 2.0)
  - 1.0)).x);
 final_noise_1 = (final_noise_1 + (0.125 * (
   (texture (sampler_noisevol_lq, (tmpvar_2 * 8.0)) * 2.0)
  - 1.0)).x);
 final_noise_1 = ((final_noise_1 + 1.0) * 0.25);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = texture (sampler_main, (((
   (uv - 0.5)
  * 
   (1.0 - (rad * 0.01))
 ) * final_noise_1) + 0.5)).xyz;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{

    float3 txr = float3(uv,q2*10);

    float final_noise = 0;
    for(int i = 0 ; i < 4 ; i++){
      final_noise += (1.0/pow(2,i))*(tex3D(sampler_noisevol_lq, txr*pow(2,i))*2-1); 
    }    

    final_noise =  (final_noise+1)*0.25;


    ret = tex2D( sampler_main, (uv-0.5)*(1-rad*0.01)*final_noise + 0.5 ).xyz; 

    // darken (decay) over time
    ret = ret; //or try: ret -= 0.004;
}
