uniform sampler2D sampler_cells;
 shader_body { 
  vec3 final_noise_1;
  vec3 ret_2;
  vec2 tmpvar_3;
  tmpvar_3 = (uv - 0.5);
  final_noise_1 = texture (sampler_cells, ((tmpvar_3 * (1.0 - 
    (bass_att * 0.1)
  )) + 0.5)).xyz;
  final_noise_1 = (1.0 - (final_noise_1 * 0.25));
  ret_2 = texture (sampler_main, (((tmpvar_3 * 
    (1.0 - (bass_att * 0.01))
  ) * final_noise_1.xy) + 0.5)).xyz;
  ret_2 = (ret_2 - (0.03 * max (1.0, 
    max (mid, treb)
  )));
  vec4 tmpvar_4;
  tmpvar_4.w = 1.0;
  tmpvar_4.xyz = ret_2;
  ret = tmpvar_4.xyz;
 }

