init_eqs_eel:
nut=0;
stp=0;stq=0;
rtp=0;rtq=0;
wvr=0;
decay=0;
dcsp=0

frame_eqs_eel:
decay=0.975;

vol=(bass+mid+treb)*0.25;
vol=vol*vol;

mtime=mtime+vol*0.01;

q1=time*0.5;


gamma=1 + min(vol*0.8,1)*0.7

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = ((texture (sampler_noise_lq, (rand_frame.xy + 
   ((uv * texsize_noise_lq.zw) * texsize.xy)
 )) * 2.0) - 1.0);
 ret_1 = (texture (sampler_pw_main, (uv + (
   (tmpvar_2.zw * texsize.zw)
  * 0.5))).xyz + ((0.59 - 
   (0.2 * rad)
 ) * tmpvar_2.xyz));
 vec3 tmpvar_3;
 tmpvar_3 = clamp (((
   (ret_1 - 0.5)
  * 3.0) + 0.25), 0.0, 1.0);
 ret_1.x = tmpvar_3.x;
 ret_1.yz = tmpvar_3.xx;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.4));
 vec3 tmpvar_2;
 tmpvar_2.x = (1.7 - (bass * 0.5));
 tmpvar_2.y = (1.7 - (treb * 0.5));
 tmpvar_2.z = (1.7 - (mid * 0.5));
 vec3 tmpvar_3;
 tmpvar_3 = pow (pow (ret_1, vec3(0.5, 0.8, 1.7)), tmpvar_2);
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    float4 N = tex2D(sampler_noise_lq, rand_frame.xy + uv*
                             texsize_noise_lq.zw*texsize.xy )*2-1;
    // sample previous frame
    ret = tex2D( sampler_pw_main, uv + N.zw*texsize.zw*0.5 ).xyz;
    ret += (0.59-0.2*rad)*N.xyz;

    ret = saturate((ret-0.5)*3 + 0.25);
    ret.yz = ret.x;    
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*0.4;

    // palettize:
    ret = pow(ret, float3(0.5, 0.8, 1.7));
    ret = pow(ret, float3(1.7-(bass*.5), 1.7-(treb*.5), 1.7-(mid*.5)));

    //ret.xyz *= 2; // a little bit of overbright
}
