warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 6.28*0.333;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 6.28*0.667;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).xyz);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).xyz);

ret *= 1.3;
}
