warp_hlsl:
shader_body
{

    float3 txr = float3(uv,q2*10);

    float final_noise = 0;


    final_noise += ((1.0/pow(2,0))*((tex3D(sampler_noisevol_hq, txr*pow(2,0))*2)-1)); 
    final_noise += ((1.0/pow(2,1))*((tex3D(sampler_noisevol_hq, txr*pow(2,1))*2)-1)); 
    final_noise += ((1.0/pow(2,2))*((tex3D(sampler_noisevol_hq, txr*pow(2,2))*2)-1)); 
    final_noise += ((1.0/pow(2,3))*((tex3D(sampler_noisevol_hq, txr*pow(2,3))*2)-1)); 

    final_noise =  (final_noise+1)*0.5;


    ret = tex2D( sampler_main, (uv-0.5)*final_noise*2 + 0.5 ).xyz; 

    // darken (decay) over time
    ret = ret - 0.1; //or try: ret -= 0.004;
}

