 vec2 v_1;
 vec3 ret_2;
 v_1 = ((normalize(
   (uv - 0.5)
 ) * aspect.xy) * (texsize.zw * 3.0));
 ret_2 = (0.25 * ((
   (texture (sampler_main, uv).xyz + texture (sampler_main, (uv + (v_1 * 2.5))).xyz)
