avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%8;


q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;
ava = ava*dec_slow + q26*(1-dec_slow);

k1 =  is_beat*equal(index%4,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;
q1 = cos(rott);
q2 = sin(rott);

q27 = 8-index;
q28 = index2/6;
q29 = .1*q27+time*.1;

trig = bnot(index%4)*q24;
