movez = movez + .02/fps*mov;
q30 = movez;


trig = q24 * bnot(index);
kmov = bnot(trig)* (kmov + .1/fps); 
q31 = kmov*82*kmov;

cont = cont + q1*.2/fps*below(index,2);
q31 = cont;





