waves.point_eqs_eel:
masterspeed=30;

tm=time*masterspeed*0.1;
perspective=0.7;


//plot x,y shape at z=0
smp=sample*6.283;

pinch=atan(smp*6 + 1.5)*0.3 + 0.7;

xp=sin(smp )*0.65;
xp=xp*pinch;
yp=cos(smp )*0.65;
yp=yp*pinch;
zp=0;


//rotate on y axis;
angy=q1*10 + sample*0;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;

//rotate on x axis
axs1 = sin(time*0.9) + 1.6;
yq= yp*cos(axs1) - zp*sin(axs1);
zq= yp*sin(axs1) + zp*cos(axs1);
yp=yq;
zp=zq;

//rotate on y axis again
axs2 = sin(time*0.45)*3.3;
xq=xp*cos(axs2) - zp*sin(axs2);
zq=xp*sin(axs2) + zp*cos(axs2);
xp=xq;
zp=zq;

//stretch y axis to compensate for aspect ratio
yp=yp*1.2;

//push forward into viewpace
zp=zp+2.1;

//project x,y,z into screenspace
xs=xp/zp;
ys=yp/zp;

//center 0,0 in middle of screen
x=(-xs)+0.5;
y=ys+0.5;

dark=1 - (cos(smp*6 + 1.5)*0.5 + 0.5);
r=1.00*dark;
g=0.32*dark;
b=0.36*dark;

