warp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2.x = (((
   ((texture (sampler_blur1, (uv + vec2(0.001, 0.0))).xyz * scale1) + bias1)
  - 
   ((texture (sampler_blur1, (uv - vec2(0.001, 0.0))).xyz * scale1) + bias1)
 ).y * 1280.0) * texsize.z);
 tmpvar_2.y = (((
   ((texture (sampler_blur1, (uv + vec2(0.0, 0.001))).xyz * scale1) + bias1)
  - 
   ((texture (sampler_blur1, (uv - vec2(0.0, 0.001))).xyz * scale1) + bias1)
 ).y * 1024.0) * texsize.w);
 ret_1.y = texture (sampler_pc_main, (uv - (tmpvar_2 * 0.001))).y;
 ret_1.y = (ret_1.y + ((
   ((ret_1.y - ((texture (sampler_blur1, uv).xyz * scale1) + bias1).y) - 0.1)
  * 0.6) + 0.27));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (vec3(0.97, 0.97, 1.0) * (vec3(1.0, 1.0, 1.0) - texture (sampler_fw_main, uv).yyy));
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{

    float1 d = 0.001;
    float1 dx = ( GetBlur1(uv + float2(d,0)) - GetBlur1(uv-float2(d,0)) ).y*1280*texsize.z;
    float1 dy = ( GetBlur1(uv + float2(0,d)) - GetBlur1(uv-float2(0,d)) ).y*1024*texsize.w;
    float2 my_uv = uv - float2(dx,dy)*0.001 + texsize.zw*float2(0,0)* tex2D( sampler_pc_main, uv).y;



    ret.y = tex2D( sampler_pc_main, my_uv).y;
    ret.y += (ret.y - GetBlur1(uv).y - 0.1)*.6 +0.27;
//ret = 0;

}

