warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 ) * 0.2));
 ret_1 = (ret_1 * 0.9);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   (vec2(-0.993, 0.993) * (uv - 0.5))
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) * (1.0 + (treb_att + 
   (mid_att * 0.5)
 )));
 vec3 tmpvar_2;
 tmpvar_2 = sqrt(ret_1);
 ret_1 = (tmpvar_2 * (tmpvar_2 + (treb_att * 0.05)));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

