frame_eqs_eel:
warp=0;
wave_r = wave_r + 0.25*(0.6*sin(0.784*time) + 0.4*sin(0.986*time));
wave_g = wave_g + 0.25*(0.6*sin(0.671*time) + 0.4*sin(1.164*time));
wave_b = wave_b + 0.25*(0.6*sin(1.423*time) + 0.4*sin(0.687*time));
bass_effect = if(above(bass_att,1.4),pow(1.1,bass_att),1);
treb_effect = if(above(treb_att,1.4),pow(0.97,treb_att),1);
net_effect = if(above(bass_att,0.8*treb_att),bass_effect,treb_effect);
zoom = net_effect;
rot = rot + rot_residual;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
shift = (tan(time*7)) -0.05;
shift = if(above(shift,0),0,if(below(shift,-0.1),-0.1,shift));
rot_residual = if(equal(bass_thresh,2),shift,rot_residual);

warp:
uniform sampler2D sampler_fw_clouds;
 shader_body { 
  vec3 ret_1;
  ret_1 = (texture (sampler_main, uv).xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1));
  ret_1 = (ret_1 + (texture (sampler_fw_clouds, uv) * 0.5).xyz);
  vec4 tmpvar_2;
  tmpvar_2.w = 1.0;
  tmpvar_2.xyz = ret_1;
  ret = tmpvar_2.xyz;
 }

comp:
uniform sampler2D sampler_smalltiled_electric_nebula;
 shader_body { 
  vec4 tmpvar_1;
  tmpvar_1 = texture (sampler_main, uv);
  vec2 tmpvar_2;
  tmpvar_2.x = ((uv.x + 0.5) + q1);
  tmpvar_2.y = ((uv.y + 0.5) + q2);
  vec4 tmpvar_3;
  tmpvar_3.w = 1.0;
  tmpvar_3.xyz = mix ((texture (sampler_smalltiled_electric_nebula, (
    (tmpvar_2 * texsize.xy)
   * texsize_smalltiled_electric_nebula.zw)).xyz * (
    ((bass + mid) + treb)
   * 0.5)), tmpvar_1.xyz, (tmpvar_1.xyz * 2.5));
  ret = tmpvar_3.xyz;
 }

warp_hlsl:
sampler sampler_fw_clouds;
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz-GetBlur1(uv);
ret+=tex2D(sampler_fw_clouds,uv)*0.5;
}

comp_hlsl:
sampler sampler_smalltiled_electric_nebula;
float4 texsize_smalltiled_electric_nebula;
shader_body
{
  ret = tex2D(sampler_main,uv).xyz;
  float2 uv2 = float2(uv.x+.5+(q1),uv.y+.5+(q2));
  float3 ret2 = tex2D(sampler_smalltiled_electric_nebula,uv2*texsize.xy*texsize_smalltiled_electric_nebula.zw).xyz;
  ret = lerp(ret2*((bass+mid+treb)*.5),ret,ret*2.5);
}
