warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur1(uv))*0.1 - 0.015;
//    ret *= 0.94;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.6 + rand_frame.xy;

float2 uv_bg = uv_orig + (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*texsize.zw*4;
ret.x = GetPixel(uv_bg) - 0.01;
ret.z *= 0.96;
}

comp_hlsl:
shader_body
{

