frame_eqs_eel:
decay = 0.25;
xx1 = xx1*0.9 + (bass)*0.01;
xx2 = xx2*0.9 + (treb)*0.01;
yy1 = yy1*0.94 + (treb+bass)*0.0075;

x1 = 0.5 + (xx1-xx2)*2;
y1 = 0.4 + yy1;

x1 = max(0,min(1,x1));
y1 = max(0,min(1,y1));
//x2 = 0;y2 = 0;x3 = 0;y3 = 0;x4 = 0;y4 = 0;

spring = 50;
grav = 2;
resist = 5;
bounce = 0.75;
dt = 0.00015*60/fps;

spx2 = (x1+x3-2*x2)*spring;
spy2 = (y1+y3-2*y2)*spring;
spx3 = (x2+x4-2*x3)*spring;
spy3 = (y2+y4-2*y3)*spring;
spx4 = (x3-x4)*spring;
spy4 = (y3-y4)*spring;

vx2 = vx2*(1-resist*dt) + dt*(spx2);
vy2 = vy2*(1-resist*dt) + dt*(spy2-grav);
vx3 = vx3*(1-resist*dt) + dt*(spx3);
vy3 = vy3*(1-resist*dt) + dt*(spy3-grav);
vx4 = vx4*(1-resist*dt) + dt*(spx4);
vy4 = vy4*(1-resist*dt) + dt*(spy4-grav);

x2 = x2 + vx2;
y2 = y2 + vy2;
x3 = x3 + vx3;
y3 = y3 + vy3;
x4 = x4 + vx4;
y4 = y4 + vy4;

vx2 = if(above(x2,0.1),vx2,abs(vx2)*bounce);
vx2 = if(below(x2,0.9),vx2,-abs(vx2)*bounce);
vx3 = if(above(x3,0.1),vx3,abs(vx3)*bounce);
vx3 = if(below(x3,0.9),vx3,-abs(vx3)*bounce);
vx4 = if(above(x4,0.1),vx4,abs(vx4)*bounce);
vx4 = if(below(x4,0.9),vx4,-abs(vx4)*bounce);

vy2 = if(above(y2,0),vy2,abs(vy2)*bounce);
vy2 = if(below(y2,1),vy2,-abs(vy2)*bounce);
vy3 = if(above(y3,0),vy3,abs(vy3)*bounce);
vy3 = if(below(y3,1),vy3,-abs(vy3)*bounce);
vy4 = if(above(y4,0),vy4,abs(vy4)*bounce);
vy4 = if(below(y4,1),vy4,-abs(vy4)*bounce);


q1 = x1;
q2 = x2;
q3 = x3;
q4 = x4;

q5 = y1;
q6 = y2;
q7 = y3;
q8 = y4;

monitor = ry4;

zoom = 1.0004;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, fract(uv)).xyz;
 ret_1 = (ret_1 + ((
   (ret_1 - mix (((texture (sampler_blur2, uv).xyz * scale2) + bias2), ((texture (sampler_blur1, uv).xyz * scale1) + bias1), uv_orig.xxx))
  * 0.3) - 0.04));
 ret_1 = (ret_1 * 0.95);
 ret_1 = (ret_1 + 0.04);
 ret_1 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.2, 0.2, 0.2));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret.xyz = tex2D( sampler_main, frac(uv) ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      float3 b = lerp(GetBlur2(uv),GetBlur1(uv),uv_orig.x);//GetBlur2(uv);
      ret.xyz += (ret.xyz - b)*0.3 - 0.04;
      ret.xyz *= 0.95;

      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xyz += 0.04;//(tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*0.3;

      // desaturate over time, to keep the globs white
      ret.xyz = lerp(ret.xyz, lum(ret.xyz), 0.2);
}
