frame_eqs_eel:
warp = 0; 
wave_r = wave_r + .2*sin(time*1.33)+bass*.3; 
wave_g = wave_g + .2*sin(time)+treb*.3; 
wave_b = wave_b + .2*sin(time*.988)+mid*.3; 
zoom = 1+bass*.01;

bassdrop = if(above(bass,bassdrop),bass,bassdrop-.05);
q1 = bassdrop;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz - (tmpvar_2.xyz * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * (2.0 + (
   max (min (q1, 1.8), 0.8)
  * 2.0)));
 ret_1 = (ret_1 - 0.01);
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, abs((ret_1 - 
   ((texture (sampler_blur3, (uv - 0.006)).xyz * scale3) + bias3)
 )), vec3((rad - 0.3)));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz * 5.0);
 ret_1 = (ret_1 + 1.2);
 vec3 tmpvar_2;
 tmpvar_2.x = (1.0 + (0.2 * sin(time)));
 tmpvar_2.y = (1.0 + (0.2 * sin(
   (time * 0.2233333)
 )));
 tmpvar_2.z = (1.0 + (0.2 * sin(
   (time * 0.733467)
 )));
 ret_1 = ((ret_1 * (
   (texture (sampler_blur2, uv).xyz * scale2)
  + bias2).x) * tmpvar_2);
 ret_1 = (ret_1 * 1.25);
 ret_1 = (ret_1 - 0.02);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    float3 blr = GetBlur3(uv-.006);
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*(2+(max(min(q1,1.8),.8)*2));
    ret = ret - .01;
    ret = lerp(ret,abs(ret-(blr)),rad-.3);
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret *= 5.00; //gamma
    ret += 1.2;
    float gb = GetBlur2(uv);
    ret = (ret*(gb))*float3(1+.2*sin(time),1+.2*sin(time*.2233333),1+.2*sin(time*.733467));
    ret *= 1.25;
    ret -= .02;

}
