uniform sampler2D sampler_fw_clouds;
 shader_body { 
  vec3 ret_1;
  ret_1 = (texture (sampler_main, uv).xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1));
  ret_1 = (ret_1 + (texture (sampler_fw_clouds, uv) * 0.5).xyz);
  vec4 tmpvar_2;
  tmpvar_2.w = 1.0;
  tmpvar_2.xyz = ret_1;
  ret = tmpvar_2.xyz;
 }

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + tmpvar_2.x);
 ret_1 = ((texture (sampler_main, (2.0 * ret_1).xy).xyz * ret_1) * 4.0);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

