sampler sampler_seaweed;

shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    float lum = ret.xz;
    ret += lum;

    float2 texc = 2*ret.xyz;
    ret = tex2D(sampler_main, texc) * ret.xyz * 4;
}
