waves.point_eqs_eel:
x=sample;
y=value1+0.5;
v=if(above((x*8+0.5) % 2,0),1,0) ;
w=if(above((y*8+0.5) % 2,0),1,0) ;
q= equal(v,w);

g=q;
b=q;
r=q;

frame_eqs_eel:
warp = 0;
wave_r = wave_r + bass*.5;
wave_g = wave_g + treb*.5;
wave_b = wave_b + mid*.5;
ib_a = ib_a + .05*sin(time*.555);
ib_size = ib_size + .24*sin(time*.444);
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
bass_flop=abs(bass_flop-1*equal(bass_thresh,2));
cx_residual = equal(bass_thresh,2)*sin(time*7) + (1-equal(bass_thresh,2))*cx_residual;
treb_thresh = above(treb_att,treb_thresh)*2 + (1-above(treb_att,treb_thresh))*((treb_thresh-1.3)*0.96+1.3);
cy_residual = equal(treb_thresh,2)*sin(time*7) + (1-equal(treb_thresh,2))*cy_residual;
mid_thresh = above(mid_att,mid_thresh)*2 + (1-above(mid_att,mid_thresh))*((mid_thresh-1.3)*0.96+1.3);
rot_residual = equal(mid_thresh,2)*sin(time*7) + (1-equal(mid_thresh,2))*rot_residual;
cx=cx+.3*cx_residual;
cy=cy+.3*cy_residual;
rot=.05*rot_residual;
timer=timer+if(bass_flop,1,-1);
q1=timer;
q2=cx_residual;
q3=cy_residual;
q4=rot_residual;
ib_r = ib_r + .4*q2;
ib_g = ib_g + .4*q3;
ib_b = ib_b + .4*q4;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 3.0));
 vec3 tmpvar_2;
 tmpvar_2 = fract((ret_1 - 0.005));
 ret_1 = tmpvar_2;
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = tmpvar_2;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1 = texture (sampler_main, uv);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ((texture (sampler_main, (
   ((hue_shader * 2.0) + q20)
 .xy + 
   fract((_qa.xy + (tmpvar_1.xy * 0.2)))
 )) * tmpvar_1.z) * 3.0).xyz;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
