warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 7.0));
 vec3 tmpvar_2;
 tmpvar_2 = fract((ret_1 - (
   (clamp (((vol / vol_att) - 1.1), 0.0, 1.0) * 0.5)
  + 0.004)));
 ret_1 = tmpvar_2;
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = tmpvar_2;
