init_eqs_eel:
beatrate=1;
avgbeatrate=1;
bbtime=1;
lbbtime=1;
avgdb=1;
fakebeatcount=0;

frame_eqs_eel:
db=bass-lbass;
lbass=bass;
vol=1+bass+mid+treb;

rs=equal(fbc%64,0);
drs=1-rs;
beatrate=1*rs+beatrate*drs;
avgbeatrate=1*rs+avgbeatrate*drs;
bbtime=1*rs+bbtime*drs;
lbbtime=1*rs+lbbtime*drs;
avgdb=1*rs+avgdb*drs;
fbc=fbc*drs+1*rs;
bc=bc*drs+1*rs;
faketime=1*rs+faketime*drs;
lftime=1*rs+lftime*drs;


avgdb=avgdb*.99+abs(db)*.01;

beatb=above(db,avgdb)*above(time-lbbtime,.5*avgbeatrate);
maxdb=max(maxdb*.99999,db);

bbtime=time*beatb;

beatrate=beatb*(bbtime-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.99+beatrate*.01)+(1-beatb)*avgbeatrate;


lbbtime=time*beatb+(1-beatb)*lbbtime;


faketime=faketime+beatrate*avgbeatrate*.01;
fakebeatb=sin(faketime*beatrate*2+3.14)*.5+.5;



fb=above(fakebeatb,.999)*above(faketime-lftime,.9*avgbeatrate);
fbc=fbc+fb;
lftime=faketime*fb;
bc=bc+fb;
monitor=fbc;
q1=equal(bc%16,0)*fb;
q2=beatbhard;
q3=equal(bc%4,0)*fb;
q4=beatmhard;
q5=beatt;
q6=beatthard;
decay=.99;
worp=0;

//dx=.005;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 3.0));
 vec3 tmpvar_2;
 tmpvar_2 = fract((ret_1 - 0.005));
 ret_1 = tmpvar_2;
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = tmpvar_2;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 tmpvar_1;
 tmpvar_1 = texture (sampler_main, uv).xxx;
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ((pow (
   mix (vec3((((texture (sampler_blur1, uv).xyz * scale1) + bias1).x + ((texture (sampler_blur3, uv).xyz * scale3) + bias3).x)), tmpvar_1, pow (tmpvar_1, vec3(0.5, 0.5, 0.5)))
 , vec3(0.5, 1.2, 8.0)) * 1.25) - 0.25);
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    ret = tex2D( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*3;

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    //ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
    ret = frac(ret - 0.005);
}

