frame_eqs_eel:
dec_med = pow (0.8, 30/fps);
dec_slow = pow (0.95, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .4+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%2;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%4;
index4 = (index4 + is_beat*bnot(index)*bnot(index2)*bnot(index3))%4;

police = dec_slow*police 
 + (1-dec_slow) * bnot (index3 + index2 -2);
