p3 = dec_med * p3+ (1-dec_med)*p2;

rott = p3 * 3.14159265359/2;

q27 = 8-index;
q28 = index2;
q26 = .0 + .1*index3;
q25 = index4;

monitor = q25;
movez = movez + .03*30/fps *
                  (1-bnot(index) * above(sin(time/20),.8));
