frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .4+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %12;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%3;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;
q27 = index + 1;
q28 = index2;

k1 =  is_beat*equal(index%2,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;
q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

drot = (index%4-2) * 30/fps;
p3 = p3*dec_slow + (1-dec_slow)*30/fps * drot;
q30 = p3 ;

stag = (q27+index2*1);
stag = max(stag,1);
p4 = dec_med*p4 + (1-dec_med)*stag;
q31 = p4;

zoom = 1 + .05*q1;
rot = -0.007 * index;

q32 = pow(0.996, 30/fps); //fade

monitor = index2;

