//complex inverted
iscale = 6;
iangle = sin(time*0.1337)*0.3;
itranslation_u = 0;
itranslation_v = 0.2;


// the mï¿½bius transformation
// z -> (az+b)/(cz-d); where a,b,c,d are complex numbers and z will be the uv-vector
// (az+b)/(cz-d) = a/c + mu/(cz+d), with mu = (bc-ad)/c
// so a/c and mu can be calculated outside of the shader

a_r = cos(angle)*scale;
a_i = sin(angle)*scale;
b_r = translation_x;
b_i = translation_y;

c_r = -cos(iangle)*iscale;
c_i = -sin(iangle)*iscale;
d_r = itranslation_u;
d_i = itranslation_v;

// c^(-1)
c_inv_r = c_r/(c_r*c_r+c_i*c_i);
c_inv_i = c_i/(c_r*c_r+c_i*c_i);

// a*c^(-1)
ac_r = (a_r*c_inv_r - a_i*c_inv_i);
ac_i = (a_r*c_inv_i - a_i*c_inv_r);

// (bc-ad)
bcad_r = (b_r*c_r - b_i*c_i)-(a_r*d_r-a_i*d_i);
bcad_i = (b_r*c_i - b_i*c_r)-(a_r*d_i-a_i*d_r);

// mu*c^(-1)
mu_r = bcad_r*c_inv_r - bcad_i*c_inv_i;
mu_i = bcad_r*c_inv_i - bcad_i*c_inv_r;

q1 = ac_r;
