warp_hlsl:
shader_body
{

    float2x2 rot =  {  q4, q5,
                       -q5, q4 };

    float3 txr = float3(uv,q2*10);

    float final_noise = 0;
    final_noise += (1.0/pow(2,0))*(tex3D(sampler_noisevol_lq, txr*pow(2,0))*2-1);
    final_noise += (1.0/pow(2,1))*(tex3D(sampler_noisevol_lq, txr*pow(2,1))*2-1);
    final_noise += (1.0/pow(2,2))*(tex3D(sampler_noisevol_lq, txr*pow(2,2))*2-1);
    final_noise += (1.0/pow(2,3))*(tex3D(sampler_noisevol_lq, txr*pow(2,3))*2-1);

    final_noise =  (final_noise+1)*0.5;


    ret = tex2D( sampler_main, mul(uv-0.5,rot)*(1-rad*0.01)*final_noise + 0.5 ).xyz; 

    // darken (decay) over time
    ret = ret; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv2;

    float4 c = float4(1,0.4,0.15,0.1);

    uv2 = uv + float2(1,0)*texsize.zw;
    float gx1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(-1,0)*texsize.zw;
    float gx2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    uv2 = uv + float2(0,1)*texsize.zw;
    float gy1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(0,-1)*texsize.zw;
    float gy2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    float3 g = normalize(float3(gx1-gx2, gy1-gy2, 0.14));

    ret = 0.5 + 0.5*g;
    ret *= saturate(length(ret.xy-0.5)*5);
    ret = ret.xxy;
    ret += 1.15;
    ret *= lerp(ret,ret*(GetBlur3(uv)-GetBlur1(uv)),pow(hue_shader.zyx,ret));
    ret *= ret;

