{

//Code bits by Flexi and Geiss

    float2 d = texsize.zw*8;
    float1 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) ).y;
    float1 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) ).y;
    float2 uv_y = uv+float2(dx,dy)*0.55;  
    float2 uv_y2 = (uv_y*1.25)-.125;  


    ret = lerp(ret,1-ret,.7*GetBlur1(uv_y)+.2*GetPixel(uv));
    ret = lerp(ret,ret-.1*GetBlur3(uv_y)+.1*GetBlur1(uv_y2)+.42*GetPixel(uv_y),.25);
    //ret *= .75;
    ret *= saturate(length(ret-0.5)*5);
    //ret = ret.xy;
    ret = lum(ret);
    ret += 1.25;
    ret *= ret*.45;
    float3 mask = GetBlur3(uv)-GetBlur1(uv)-.25*GetBlur2(uv_y);
    ret *= lerp(.9*ret,1.2*ret*mask,pow(hue_shader.zxy,ret));
    ret += .02;
    ret *= ret;
    ret *= hue_shader.zxy;
    ret *= 2;

