frame_eqs_eel:
warp=bass*.2;
warp=warp+(above(bass,1)*bass*.2);

echo_alpha=(above(bass,1.25)*.9);

//mv_a=(above(bass,1)*bass*.5);

// Awesome beat code by martin!
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;

FR = if(is_beat,if(above(FR,0),-1,1),FR);

q2=q2+(above(bass,1.3)*sin(time)*.02);

rot=(rot+q2)*FR;

zoom=zoom+(above(bass,1.3)*bass*.03);

sy=sy+(above(treb_att,1.2)*treb_att*.002*sin(time));
sx=sx+(above(treb_att,1.2)*treb_att*.002*sin(time));

