shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 float tmpvar_3;
 tmpvar_3 = ((texture (sampler_blur2, uv).xyz * scale2) + bias2).x;
 ret_1 = (tmpvar_2.xyz * 2.4);
 ret_1 = (ret_1 * texture (sampler_main, (tmpvar_3 + (tmpvar_3 * tmpvar_2.xyz).xy)).xyz);
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_noisevol_hq, (((vec3(0.05, 0.05, 0.0) * uv.xyy) * (
   (q28 * texsize.xyy)
  * texsize_noisevol_hq.zww)) + ((time * vec3(0.0, 0.0, 1.0)) * q31)));
 if (((ret_1.x > (q26 * q3)) && (ret_1.x <= (q23 * q1)))) {
   ret_1.z = (ret_1.z - (tmpvar_4.x * 0.5));
