warp_hlsl:
shader_body
{
    ret = tex2D( sampler_fw_main, uv ).xyz;

    float2 offset = float2(roam_cos.x, roam_sin.x)*0.8;
    float tex = tex2D(sampler_noise_hq, uv - offset);
    ret = lerp(ret, 8*tex, 0.015);    

    ret *= float3(.918,.912,.905);
}

