repeats=10;
samplerepeat=sample*repeats;
cycle=int(samplerepeat);
sampcyc=samplerepeat - cycle;
cycleto1=cycle/repeats;

n=sampcyc*6.283;
phs=sampcyc;
tm=q1+phs+(cycleto1)*0.1;
tmm=time;
rsample=(1-sampcyc*0.9)*2.2*(cycleto1+1);
a=sampcyc * below(sampcyc,0.95);
